import React, { useContext } from 'react';
import MyContext from '../context/MyContext';

function FinalPrice() {
  const { chosenNumbers, gameName, finalPrice, setFinalPrice } = useContext(MyContext);

  const priceTableSena = () => {
    switch (chosenNumbers.length) {
      case 6:
        return 4.50;
      case 7:
        return 31.50;
      case 8:
        return 126;
      case 9:
        return 378;
      case 10:
        return 945;
      case 11:
        return 2079;
      case 12:
        return 4158;
      case 13:
        return 7722;
      case 14:
        return 13513.50;
      case 15:
        return 22522.50;
      default:
        return 0;
    }
  }

  const priceTableQuina = () => {
    switch(chosenNumbers.length) {
      case 5:
        return 2;
      case 6:
        return 12;
      case 7:
        return 42;
      case 8:
        return 112;
      case 9:
        return 252;
      case 10:
        return 504;
      case 11:
        return 924;
      case 12:
        return 1584;
      case 13:
        return 2574;
      case 14:
        return 4004;
      case 15:
        return 6006;
      default:
        return 0;
    }
  }

  useEffect(() => {
    const creatingNumbers = () => {
      let numbers = [];
      if (gameName === 'Sena') {
        const MIN_SENA_CARD = 1;
        const MAX_SENA_CARD = 60;
        let lengthSena = MIN_SENA_CARD;
        while (lengthSena <= MAX_SENA_CARD) {
          numbers.push(lengthSena);
          lengthSena += 1;
        }
      } else {
        const MIN_QUINA_CARD = 0;
        const MAX_QUINA_CARD = 80;
        let lengthQuina = MIN_QUINA_CARD;
        while (lengthQuina <= MAX_QUINA_CARD) {
          numbers.push(lengthQuina);
          lengthQuina += 1;
        }
      }
      const turnNumbersToString = numbers
        .map((num) => num.toString())
        .map((str) => str.length < 2 ? '0' + str : str);
      return setCardNumbers(turnNumbersToString);
    }
    creatingNumbers();
  }, [gameName, setCardNumbers]);



  return (
    <h3>
      {
        gameName === 'Sena' ?
          `Valor do Jogo: R$ ${priceTableSena().toFixed(2).toString().replace('.', ',')}`
          : `Valor do Jogo: R$ ${priceTableQuina().toFixed(2).toString().replace('.', ',')}`
      }
    </h3>
  );
}

export default FinalPrice;
