import React, { useContext, useState } from 'react';
import MyContext from '../context/MyContext';

function SenaCards() {
  const { cardNumbers } = useContext(MyContext);
  const [chosenNumbers, setChosenNumbers] = useState([]);

  const handleClick = (number) => {
    const numberNotExists = chosenNumbers.find((item) => item === number);
    setChosenNumbers([...chosenNumbers, number]);
    console.log("chosenNumbers puro", chosenNumbers);
    if (!numberNotExists) {
      console.log("entrou no if", numberNotExists);
      console.log("chosenNumbers no if", chosenNumbers);
    } else {
      const newChosenNumbers = chosenNumbers.filter((item) => item !== number);
      console.log("entrou no else", newChosenNumbers);
      setChosenNumbers([newChosenNumbers]);
      console.log("chosenNumbers no else", chosenNumbers);
    }
  }

  return (
    <div>
      {cardNumbers.map((num) => (
        <span key={num} onClick={() => handleClick(num) }>
          { num }
        </span>
        ))}
    </div>
  );
}

export default SenaCards;
