import React, { useContext, useEffect } from 'react';
import MyContext from '../context/MyContext';

function FinalPrice() {
  const { chosenNumbers, gameName, finalPrice, setFinalPrice } = useContext(MyContext);

  const priceTableSena = () => {
    switch (chosenNumbers.length) {
      case 6:
        return 4.50;
      case 7:
        return 31.50;
      case 8:
        return 126;
      case 9:
        return 378;
      case 10:
        return 945;
      case 11:
        return 2079;
      case 12:
        return 4158;
      case 13:
        return 7722;
      case 14:
        return 13513.50;
      case 15:
        return 22522.50;
      default:
        return 0;
    }
  }

  const priceTableQuina = () => {
    switch(chosenNumbers.length) {
      case 5:
        return 2;
      case 6:
        return 12;
      case 7:
        return 42;
      case 8:
        return 112;
      case 9:
        return 252;
      case 10:
        return 504;
      case 11:
        return 924;
      case 12:
        return 1584;
      case 13:
        return 2574;
      case 14:
        return 4004;
      case 15:
        return 6006;
      default:
        return 0;
    }
  }

  useEffect(() => {
    const price = () => {
      gameName === 'Sena' ?
        setFinalPrice(
          `Valor do Jogo: R$ ${priceTableSena().toFixed(2).toString().replace('.', ',')}`
        )
        : setFinalPrice(
          `Valor do Jogo: R$ ${priceTableQuina().toFixed(2).toString().replace('.', ',')}`
        )
    }
    price();
  }, [gameName, priceTableSena]);



  return (
    <h3>{ finalPrice }</h3>
  );
}

export default FinalPrice;
